function openSearchForm(){const e=document.querySelector("body"),c=document.querySelector(".search-link"),r=document.querySelector(".header-search"),s=document.querySelector(".header__right"),a=document.querySelector(".main"),t=document.querySelector(".footer"),o=document.querySelector(".nav__close-search");c.addEventListener("click",()=>{r.classList.add("header-search--active"),o.classList.add("nav__close-search--active"),s.classList.add("header__right--hide"),a.classList.add("main--hide"),t.classList.add("footer--hide"),e.style.overflow="hidden"}),o.addEventListener("click",()=>{r.classList.remove("header-search--active"),o.classList.remove("nav__close-search--active"),s.classList.remove("header__right--hide"),a.classList.remove("main--hide"),t.classList.remove("footer--hide"),e.style.overflow=""})}openSearchForm();